SELECT t1.id, users.name AS name, t2.name AS course, schools.name AS school, educations."endDate" FROM educations JOIN courses t1 ON educations."courseId"=t1.id JOIN users ON educations."userId"=users.id JOIN courses t2 ON educations."courseId"=t2.id JOIN schools ON educations."schoolId"=schools.id WHERE educations.status='finished' AND users.id=30 ORDER BY t1.id;