SELECT schools.id, schools.name AS school, courses.name AS course, companies.name AS company, roles.name AS role FROM applicants JOIN educations ON applicants."userId"=educations."userId" JOIN schools ON schools.id=educations."schoolId" JOIN courses ON courses.id=educations."courseId" JOIN jobs ON jobs.id=applicants."jobId" JOIN companies ON companies.id=jobs."companyId" JOIN roles ON roles.id=jobs."roleId" WHERE roles.name='Software Engineer' AND companies.id=10 AND jobs.active=true ORDER BY schools.id;
